const message = node.properties.message.value || 'Alert triggered!';
const appendData = node.properties.showData.value;
let finalMessage = message;
if (appendData && data) {
  const dataStr = JSON.stringify(data, null, 2);
  finalMessage = message + '\n\nData: ' + dataStr;
}
helpers.alert(finalMessage);
return { ...data, alerted: true, alertMessage: finalMessage };
