helpers.log('Starting Split node');
const fieldName = node.properties.fieldToSplit.value;
let sourceArray = data;

if (fieldName) {
  helpers.log(`Splitting field: ${fieldName}`);
  sourceArray = data[fieldName];
} else {
  helpers.log('Splitting entire input data');
}

if (!Array.isArray(sourceArray)) {
   helpers.warn('Split node input is not an array. Passing through.');
   return [data];
}

helpers.log(`Splitting array into ${sourceArray.length} individual items`);
return sourceArray;
