const title = node.properties.title.value || 'Notification';
let description = node.properties.description.value || '';
const variant = node.properties.variant.value || 'default';
const includeData = node.properties.includeData.value;

if (includeData && data) {
  const dataStr = JSON.stringify(data, null, 2);
  description = description ? description + '\n\n' + dataStr : dataStr;
}

// Show toast notification
helpers.toast({
  title: title,
  description: description || undefined,
  variant: variant
});

// Return data to continue workflow
return {
  ...data,
  toastShown: true,
  toastTitle: title
};
