helpers.log('Starting Wait node');
const durationSeconds = node.properties.duration.value;
const durationMs = durationSeconds * 1000;
helpers.log(`Waiting for ${durationSeconds} second${durationSeconds !== 1 ? 's' : ''} (${durationMs}ms)`);
const startTime = Date.now();
await new Promise(resolve => setTimeout(resolve, durationMs));
const elapsed = Date.now() - startTime;
helpers.log(`Wait completed (actual: ${(elapsed / 1000).toFixed(2)}s)`);
return { resumed: true, waitedSeconds: durationSeconds, actualMs: elapsed };
